package Tomasulo;public class RegisterFileEntry {	public boolean busyBit;	public Operand operand;		public RegisterFileEntry() {		this.busyBit = false;		this.operand = new Operand(0,0);	}		public RegisterFileEntry(int value) {		this.busyBit = false;		this.operand = new Operand(-1,value);	}		public boolean isBusy()	{		return busyBit;	}		public int getTag()	{		if(this.isBusy())			return this.operand.tagBit;		return -1;	}		public int getValue()	{		if(this.isBusy())			return Integer.MIN_VALUE;		return this.operand.getValue();	}		public void setTag(int tag)	{		this.busyBit = true;		this.operand.setTag(tag);	}		public void setValue(int value)	{		this.busyBit = false;		this.operand.setValue(value);	}}